mapper.select:
select

,(select name from sys_project p where p.id = pro_id) proName
from sys_authority a

  and a.is_del = 0

    and id = #{id,jdbcType=VARCHAR}


    and auth_key = #{authKey,jdbcType=VARCHAR}

select
,(select name from sys_project p where p.id = pro_id) proName from sys_authority a
and a.is_del = 0
and create_time>= concat(#{startCreateTime,jdbcType=VARCHAR}, '00:00:00')
and create_time<= concat(#{endCreateTime,jdbcType=VARCHAR}, '23:59:59')
and auth_name like concat('%', #{authName,jdbcType=VARCHAR}, '%')
and pro_id = #{proId,jdbcType=INTEGER}
and type = #{type,jdbcType=TINYINT}
and parent_id = #{parentId,jdbcType=INTEGER}
ORDER BY create_time DESC
